/**
 * 获取时间戳，例如：1682583902000
 * @return int
 */
function get_time_stamp()
{
    list($msec, $sec) = explode(' ', microtime());
    $msectime = (float)sprintf('%.0f', (floatval($msec) + floatval($sec)) * 1000);
    return intval($msectime);
}

/**
 * 取出中间文本
 * @param $str
 * 预取全文本
 * @param $leftStr
 * 左边文本
 * @param $rightStr
 * 右边文本
 * @return string
 */
function getSubstr($str, $leftStr, $rightStr)
{
    $left = strpos($str, $leftStr);
    //echo '左边:'.$left;
    $right = strpos($str, $rightStr, $left);
    //echo '<br>右边:'.$right;
    if ($left < 0 or $right < $left) return '';
    return substr($str, $left + strlen($leftStr), $right - $left - strlen($leftStr));
}

/**
 * 1、登录获取Code
 * @param $user
 * 登录账户
 * @param $password
 * 密码
 * @return string
 * 返回登录成功的code
 */
function loginGetCode($user, $password)
{
    function headerHandler($curl, $headerLine)
    {
        $len = strlen($headerLine);
        // HTTP响应头是以:分隔key和value的
        $split = explode(':', $headerLine, 2);
        if (count($split) > 1) {
            $key = trim($split[0]);
            $value = trim($split[1]);
            // 将响应头的key和value存放在全局变量里
            $GLOBALS['G_HEADER'][$key] = $value;
        }
        return $len;
    }

    $curl = curl_init();
    if ($GLOBALS['$is_phone']) {
        $url_user = '+86' . $user;
    } else {
        $url_user = $user;
    }

    curl_setopt_array($curl, [
        CURLOPT_URL => "https://api-user.huami.com/registrations/" . $url_user . "/tokens",
        CURLOPT_RETURNTRANSFER => true,
        CURLOPT_ENCODING => "",
        CURLOPT_MAXREDIRS => 10,
        CURLOPT_TIMEOUT => 30,
        CURLOPT_HTTP_VERSION => CURL_HTTP_VERSION_1_1,
        CURLOPT_CUSTOMREQUEST => "POST",
        CURLOPT_POSTFIELDS => "client_id=HuaMi&password=" . $password . "&redirect_uri=https://s3-us-west-2.amazonaws.com/hm-registration/successsignin.html&token=access",
        CURLOPT_HTTPHEADER => [
            "Content-Type: application/x-www-form-urlencoded;charset=UTF-8",
            "User-Agent: Mozilla/5.0 (iPhone; CPU iPhone OS 14_7_1 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Version/14.1.2",
            "content-type: application/x-www-form-urlencoded"
        ],

    ]);
    curl_setopt($curl, CURLOPT_HEADERFUNCTION, "headerHandler"); // 设置header处理函数
//    curl_setopt($curl, CURLOPT_FOLLOWLOCATION, 1);//跟随位置
    //响应内容
    $response = curl_exec($curl);
    $err = curl_error($curl);

    $location = $GLOBALS['G_HEADER']['Location'];
//    var_dump($location);
    //获取code
    $code = getSubstr($location, "&access=", "&");
    curl_close($curl);
    if ($err) {
        return "";
    } else {
        return $code;
    }


}

/**
 * 2、获取login_token和user_id
 * @param $code
 * code
 * @return object
 * token_info对象，里面成员有login_token和user_id
 */
function getLoginToken($code)
{

    $curl = curl_init();

    if ($GLOBALS['$is_phone']) {
        $post_data = "code=" . $code . "&app_name=com.xiaomi.hm.health&app_version=4.6.0&country_code=CN&device_id=2C8B4939-0CCD-4E94-8CBA-CB8EA6E613A1&device_model=phone&grant_type=access_token&third_name=huami_phone";
    } else {
        $post_data = "code=" . $code . "&app_name=com.xiaomi.hm.health&app_version=6.3.5&country_code=CN&device_id=2C8B4939-0CCD-4E94-8CBA-CB8EA6E613A1&device_model=phone&dn=api-user.huami.com%2Capi-mifit.huami.com%2Capp-analytics.huami.com&grant_type=access_token&lang=zh_CN&os_version=1.5.0&source=com.xiaomi.hm.health&third_name=email";
    }
    curl_setopt_array($curl, [
        CURLOPT_URL => "https://account.huami.com/v2/client/login",
        CURLOPT_RETURNTRANSFER => true,
        CURLOPT_ENCODING => "",
        CURLOPT_MAXREDIRS => 10,
        CURLOPT_TIMEOUT => 30,
        CURLOPT_HTTP_VERSION => CURL_HTTP_VERSION_1_1,
        CURLOPT_CUSTOMREQUEST => "POST",
        CURLOPT_POSTFIELDS => $post_data,
        CURLOPT_HTTPHEADER => [
            "Content-Type: application/x-www-form-urlencoded;charset=UTF-8",
            "User-Agent: Mozilla/5.0 (iPhone; CPU iPhone OS 14_7_1 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Version/14.1.2",
            "content-type: application/x-www-form-urlencoded"
        ],
    ]);

    $response = curl_exec($curl);
    $err = curl_error($curl);

    curl_close($curl);

    if ($err) {
        return "";
    } else {
        $res = json_decode($response);
        $token_info = $res->token_info;

        return $token_info;
    }
}

/**
 * 3、获取app_token
 * @param $login_token
 * @return string
 */
function getAppToken($login_token)
{

    $curl = curl_init();

    curl_setopt_array($curl, [
        CURLOPT_URL => "https://account-cn.huami.com/v1/client/app_tokens?app_name=com.xiaomi.hm.health&dn=api-user.huami.com,api-mifit.huami.com,app-analytics.huami.com&login_token=" . $login_token,
        CURLOPT_RETURNTRANSFER => true,
        CURLOPT_ENCODING => "",
        CURLOPT_MAXREDIRS => 10,
        CURLOPT_TIMEOUT => 30,
        CURLOPT_HTTP_VERSION => CURL_HTTP_VERSION_1_1,
        CURLOPT_CUSTOMREQUEST => "GET",
    ]);

    $response = curl_exec($curl);
    $err = curl_error($curl);

    curl_close($curl);

    if ($err) {
        return "";
    } else {
        $res = json_decode($response);
        return $res->token_info->app_token;
    }
}

